import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Row, Col } from "react-bootstrap";

import { KPICard } from "./KPICard.js";

import TotalGroupsGraph from "./charts/graph/TotalGroupsGraph";
import MoneyTotalGraph from "./charts/graph/MoneyTotalGraph";
import UsersTotalGraph from "./charts/graph/UsersTotalGraph";

import SoMeCircleChart from "./charts/circle/SoMeCircleChart";
import GroupSizeChart from "./charts/circle/GroupSizeChart";

import UsersLastMonthBar from "./charts/bar/UsersLastMonthBar";
import UsersLastYearBar from "./charts/bar/UsersLastYearBar";

import { fetchUserStats } from "../../redux/actions/KPI/UserStatsAction";
import { fetchGroupTotal } from "../../redux/actions/KPI/GroupTotalAction";
import { getCurrentTime } from "../../util/Date";

class KPIView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      usersTotal: "",
      usersTotalLastUpdate: "",
      usersToday: "",
      usersTodayLastUpdate: "",
      groupsTotal: "",
      groupsTotalLastUpdate: "",
      $Total: ""
    };
  }
  async componentDidMount() {
    // Error handling when not authenticated?
    const temp = await this.props.fetchUserStats();
    const temp1 = await this.props.fetchGroupTotal();

    const userStats = this.props.userStats;
    const groupTotal = this.props.groupTotal;

    console.log(this.props.groupTotal);

    let lastUpdatedAt = getCurrentTime();

    this.setState({
      usersTotal: userStats.numberOfUsers,
      usersTotalLastUpdate: lastUpdatedAt,
      usersToday: userStats.signups[10].count,
      usersTodayLastUpdate: lastUpdatedAt,

    });

    // Reload KPI data
    setInterval(async () => {
      // Error handling when not authenticated?
      const m = await this.props.fetchUserStats();

      let lastUpdatedAt = getCurrentTime();

      this.setState({
        usersTotal: this.props.userStats.numberOfUsers,
        usersTotalUpdate: lastUpdatedAt
      });
    }, 100000);
  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-user text-warning" />}
                statsText="Total Users"
                statsValue={this.state.usersTotal}
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText={`Last Update: ${this.state.usersTotalLastUpdate}`}
              />
            </Col>

            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="New Users Today"
                statsValue={this.state.usersToday}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText={`Last Update: ${this.state.usersTodayLastUpdate}`}
              />
            </Col>
            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-wallet text-success" />}
                statsText="Amount Registered"
                statsValue="$5,345"
                statsIcon={<i className="fa fa-calendar-o" />}
                statsIconText="Last day"
              />
            </Col>

            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="fa fa-twitter text-info" />}
                statsText="Social Media Followers"
                statsValue="+45"
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
          </Row>

          <Row>
            <Col lg={4} sm={6}>
              <UsersTotalGraph />
            </Col>
            <Col lg={4} sm={6}>
              <TotalGroupsGraph />
            </Col>
            <Col lg={4} sm={6}>
              <MoneyTotalGraph />
            </Col>
          </Row>
          <Row>
            <Col lg={4} sm={6}>
              <SoMeCircleChart />
            </Col>
            <Col lg={4} sm={6}>
              <UsersLastMonthBar />
            </Col>

            <Col lg={4} sm={6}>
              <UsersLastYearBar />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    userStats: state.KPI.userStats,
    groupTotal: state.KPI.groupTotal
  };
};

export default connect(mapStateToProps, { fetchUserStats, fetchGroupTotal })(KPIView);
