import { fetchFromServer } from "../Fetch";
import { KEY_STATS } from "../ActionTypes";

export const fetchKeyStats = () => async dispatch => {
  let keyStats = {
    groupStats: "",
    userStats: "",
    shareStats: "",
    meetingStats: ""
  };

  const groupQuery = `query{
    groupStats{
      groupTotal    
     groupsLastMonth{
        data{
          count
          day{
            year
            month
            day
          }     
        }
    }
    groupsLastYear{
        data{
          year
          month
          count  
        }
      }

    }
    }`;

  const groupResponse = await fetchFromServer("post", groupQuery);

  keyStats.groupStats = groupResponse.data.data.groupStats;

  const userQuery = `query{
      userStats{
        userCount   
        userGenderStats{
          value
          count
         }
        usersLastYear{
          data{
            year
            month
            count
          }
        }       
      }
      }`;

  const userResponse = await fetchFromServer("post", userQuery);

  keyStats.userStats = userResponse.data.data.userStats;

  const meetingQuery = `query {
      meetingStats{
        meetingTotal
        meetingsLastYear{
          data{
            year
            month
            count
          }
        }            
      }
    }`;

  const meetingResponse = await fetchFromServer("post", meetingQuery);

  keyStats.meetingStats = meetingResponse.data.data.meetingStats;

  const shareQuery = `query {
      financeStats{
        shareStats {
          shareTotal
        }

      }
    }`;

  const shareResponse = await fetchFromServer("post", shareQuery);

  keyStats.shareStats =
    shareResponse.data.data.financeStats.shareStats.shareTotal;

  console.log(keyStats);

  dispatch({
    type: KEY_STATS,
    payload: keyStats
  });
};
