import React, { Component } from "react";

import BootstrapTable from "react-bootstrap-table-next";

import paginationFactory from "react-bootstrap-table2-paginator";

import { Grid, Row, Col } from "react-bootstrap";
import { FormGroup, ControlLabel, FormControl } from "react-bootstrap";

import Sidebar from "../navigation/Sidebar";
import Header from "../navigation/Header";

class NGOGroupView extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    console.log(this.props);
  }

  render() {
    const groupData = this.props.location.state.groupInfo;
    let groupInfo = [];

    for (let key in groupData) {
      groupInfo.push(groupData[key]);
    }

    const properties1 = [
      "Group Name",
      "Registration Date",
      "Currency",
      "Cycle",
      "Box Balance",
      "Last Meeting",
      "Loan Service Fee",
      "Loan Limit",
      "Amount Per Share"
    ];

    const properties2 = [
      "Total Meetings",
      "Meetings in Cycle",
      "Total Loans",
      "Loans in Cycle",
      "Total Shares",
      "Shares In Cycle",
      "Owner",
      "Admin"
    ];

    const columns = [
      {
        dataField: "name",
        text: "Members"
      }
    ];

    const renderGroupInfo = (properties, groupInfo, start) => {
      let counter = 0;

      while (counter < 9) {
        groupInfo.map(element => {
          return (
            <Row>
              <div className="col-md-5">
                <FormGroup>
                  <ControlLabel>{properties[counter]}</ControlLabel>
                  <FormControl
                    type="text"
                    placeholder={properties[counter]++}
                    value={groupInfo[start++]}
                  />
                </FormGroup>
              </div>
            </Row>
          );
        });
      }
    };
    return (
      <div className="wrapper">
        <Sidebar />
        <div id="main-panel" className="main-panel" ref="mainPanel">
          <Header title="Group Overview" />
          <div className="content">
            <Grid fluid>
              <Row>
                <Col lg={3}>{renderGroupInfo(properties1, groupInfo, 0)}</Col>
                <Col lg={3}> {renderGroupInfo(properties2, groupInfo, 9)}</Col>

                <Col lg={3}>
                  <BootstrapTable
                    keyField="id"
                    data={groupInfo.members}
                    columns={columns}
                    pagination={paginationFactory()}
                  />
                </Col>
              </Row>
            </Grid>
          </div>
        </div>
      </div>
    );
  }
}

export default NGOGroupView;
