import axios from "axios";
import { KEY_STATS } from "../ActionTypes";

const url = "/graphql";

export const fetchKeyStats = () => async dispatch => {
  let groupResponse, userResponse, meetingResponse, shareResponse;

  let keyStats = {
    groupStats: "",
    userStats: "",
    genderStats: "",
    shareStats: "",
    meetingStats: ""
  };
  try {
    const groupQuery = `query{
    groupStats{
      groupTotal    
     groupsLastMonth{
        data{
          count
          day{
            year
            month
            day
          }     
        }
    }
    groupsLastYear{
        data{
          month
          count  
        }
      }

    }
    }`;

    groupResponse = await axios({
      url,
      method: "post",
      data: {
        query: groupQuery
      }
    });

    keyStats.userGroup = groupResponse.data.data.groupStats;

    const userQuery = `query{
      userStats{
        userCount   
        userGender{
          value
          count
         }
        usersLastYear{
          data{
            month
            count
          }
        }       
      }
      }`;

    userResponse = await axios({
      url,
      method: "post",
      data: {
        query: userQuery
      }
    });

    keyStats.userStats = userResponse.data.data.userStats;

    const meetingQuery = `query {
      meetingStats{
        meetingTotal
        meetingsLastYear{
          data{
            month
            count
          }
        }            
      }
    }`;

    meetingResponse = await axios({
      url,
      method: "post",
      data: {
        query: meetingQuery
      }
    });

    console.log(meetingResponse);

    keyStats.meetingStats = meetingResponse.data.data.meetingStats;

    // let response;

    // response = await axios({
    //   url,
    //   method: "post",
    //   data: {
    //     query: data
    //   }
    // });

    // dispatch({
    //   type: KEY_STATS,
    //   payload: response.data.data.keyStats
    // });
  } catch (err) {
    console.log(err);
  }
};
