import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Row, Col } from "react-bootstrap";
import { KPICard } from "../util/KPICard";

import Sidebar from "../../navigation/Sidebar";
import Header from "../../navigation/Header";

import TotalGraph from "../charts/graph/TotalGraph";
import SizeChart from "../charts/circle/SizeChart";

import LastMonthBar from "../charts/bar/LastMonthBar";
import LastYearBar from "../charts/bar/LastYearBar";

import { fetchEngagementStats } from "../../../redux/actions/kpi/EngagementStatsAction";
import { getCurrentTime } from "../../../util/Date";

class EngagementView extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }
  componentDidMount() {
    this.fetchData();

    setInterval(async () => {
      this.fetchData();
    }, 10000000);
  }

  async fetchData() {
    await this.props.fetchEngagementStats();

    const lastUpdatedAt = getCurrentTime();

    const engagementStats = this.props.engagementStats;

    this.setState({
      groupsActive: engagementStats.groupEngagement.groupsActive,
      groupActivity: engagementStats.groupEngagement.groupMeetingFrequency,
      usersActive: engagementStats.userEngagement,
      ngosActive: "",
      meetings: "",

      lastUpdate: lastUpdatedAt
    });
  }

  render() {
    const KPICards = {
      usersActive: { text: "Active Users", icon: "pe-7s-graph1 text-danger" },
      groupsActive: { text: "Active Groups", icon: "pe-7s-users text-info" },
      ngosActive: {
        text: "Active NGO's",
        icon: "pe-7s-users text-info"
      },
      meetings: {
        text: "Meetings in Active Groups",
        icon: "pe-7s-users text-info"
      }
    };
    return (
      <div className="wrapper">
        <Sidebar />
        <div id="main-panel" className="main-panel" ref="mainPanel">
          <Header title="Engagement" />
          <div className="content">
            <Grid fluid>
              <Row>
                {Object.keys(KPICards).map((kpi, index) => (
                  <Col lg={3} sm={6}>
                    <KPICard
                      bigIcon={<i className={KPICards[kpi].icon} />}
                      statsText={KPICards[kpi].text}
                      statsValue={this.state[kpi]}
                      statsIcon={<i className="fa fa-refresh" />}
                      statsIconText={`Last Update: ${this.state.lastUpdate}`}
                    />
                  </Col>
                ))}
              </Row>

              <Row>
                <Col lg={4} sm={6}>
                  <TotalGraph title="Engagement" stroke="#228b22" data="" />
                </Col>
              </Row>
              <Row>
                <Col lg={4} sm={6}>
                  <LastMonthBar title="Engagement" color="#228b22" data="" />
                </Col>
                <Col lg={4} sm={6}>
                  <SizeChart
                    title="Months Since Last Meeting"
                    colors={["#ff0000", "#1828E8", "#228b22", "#2196f3"]}
                    data={this.state.groupActivity}
                  />
                </Col>
              </Row>
            </Grid>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    engagementStats: state.KPI.engagementStats
  };
};

export default connect(mapStateToProps, { fetchEngagementStats })(
  EngagementView
);
