import React, { Component } from "react";
import { Col } from "react-bootstrap";
import {
  ResponsiveContainer,
  LineChart,
  Line,
  CartesianGrid,
  YAxis,
  XAxis
} from "recharts";

class GraphChart extends Component {
  // constructor(props) {
