import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Row, Col } from "react-bootstrap";
import { KPICard } from "../util/KPICard";

import Sidebar from "../../navigation/Sidebar";
import Header from "../../navigation/Header";

import TotalGraph from "../charts/graph/TotalGraph";
import SizeChart from "../charts/circle/SizeChart";

import LastMonthBar from "../charts/bar/LastMonthBar";
import LastYearBar from "../charts/bar/LastYearBar";

import { fetchEngagementStats } from "../../../redux/actions/kpi/EngagementStatsAction";
import { getCurrentTime } from "../../../util/Date";

class EngagementView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      groupActivity: "",
      lastUpdate: ""
    };
  }
  componentDidMount() {
    this.fetchData();

    setInterval(async () => {
      this.fetchData();
    }, 10000000);
  }

  async fetchData() {
    await this.props.fetchEngagementStats();

    const lastUpdatedAt = getCurrentTime();

    const engagementStats = this.props.engagementStats;

    this.setState({
      groupActivity: engagementStats.groupActivity,
      lastUpdate: lastUpdatedAt
    });
  }

  render() {
    return (
      <div className="wrapper">
        <Sidebar />
        <div id="main-panel" className="main-panel" ref="mainPanel">
          <Header title="Engagement" />
          <div className="content">
            <Grid fluid>
              <Row>
                <Col lg={3} sm={6}>
                  <KPICard
                    bigIcon={<i className="pe-7s-graph1 text-danger" />}
                    statsText="Total Meetings"
                    statsValue=""
                    statsIcon={<i className="fa fa-refresh" />}
                    statsIconText={`Last Update: ${this.state.lastUpdate}`}
                  />
                </Col>

                <Col lg={3} sm={6}>
                  <KPICard
                    bigIcon={<i className="pe-7s-users text-info" />}
                    statsText=""
                    statsValue=""
                    statsIcon={<i className="fa fa-clock-o" />}
                    statsIconText={`Last Update: ${this.state.lastUpdate}`}
                  />
                </Col>
                <Col lg={3} sm={6}>
                  <KPICard
                    bigIcon={<i className="pe-7s-users text-info" />}
                    statsText="This Month"
                    statsValue=""
                    statsIcon={<i className="fa fa-refresh" />}
                    statsIconText={`Last Update: ${this.state.lastUpdate}`}
                  />
                </Col>
                <Col lg={3} sm={6}>
                  <KPICard
                    bigIcon={<i className="pe-7s-users text-info" />}
                    statsText="Last Year"
                    statsValue=""
                    statsIcon={<i className="fa fa-refresh" />}
                    statsIconText={`Last Update: ${this.state.lastUpdate}`}
                  />
                </Col>
              </Row>

              <Row>
                <Col lg={4} sm={6}>
                  <TotalGraph title="Total Meetings" stroke="#228b22" data="" />
                </Col>
                <Col lg={4} sm={6}>
                  <SizeChart
                    title="Group Engagement"
                    colors={[
                      "#a4de6c",
                      "#67b6ed",
                      "#8884d8",
                      "#ff0000",
                      "#2196f3",
                      "#228b22"
                    ]}
                    data={this.state.groupSize}
                  />
                </Col>
              </Row>
              <Row>
                <Col lg={4} sm={6}>
                  <LastMonthBar
                    title="Meetings Last Month"
                    color="#228b22"
                    data=""
                  />
                </Col>

                <Col lg={4} sm={6}>
                  <LastYearBar
                    title="Meetings Last Year"
                    color="#ff0000"
                    data=""
                  />
                </Col>
              </Row>
            </Grid>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    engagementStats: state.KPI.engagementStats
  };
};

export default connect(mapStateToProps, { fetchEngagementStats })(
  EngagementView
);
