import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Row, Col } from "react-bootstrap";
import { KPICard } from "../util/KPICard";

import TotalGraph from "../charts/graph/TotalGraph";
import GroupSizeChart from "../charts/circle/GroupSizeChart";

import LastMonthBar from "../charts/bar/LastMonthBar";
import LastYearBar from "../charts/bar/LastYearBar";

import { fetchMeetingStats } from "../../../redux/actions/KPI/MeetingStatsAction";
import { getCurrentTime } from "../../../util/Date";

class GroupView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      meetingTotal: "",
      meetingTotalLastUpdate: "",
      meetingsToday: "",
      meetingTodayLastUpdate: "",
      meetingMonth: "",
      meetingYear: "",
      meetingsLastMonth: "",
      meetingsLastYear: ""
    };
    this.fetchData = this.fetchData.bind(this);
  }
  componentDidMount() {
    // Error handling when not authenticated?
    this.fetchData();

    // Reload KPI data
    setInterval(async () => {
      // Error handling when not authenticated?
      this.fetchData();
    }, 10000000);
  }

  async fetchData() {
    await this.props.fetchGroupStats();

    const meetingStats = this.props.meetingStats;
    let lastUpdatedAt = getCurrentTime();

    let meetingMonthCount = 0;
    let meetingYearCount = 0;

    meetingStats.meetingsLastMonth.data.forEach(element => meetingMonthCount += element.count);
    meetingStats.meetingsLastYear.data.forEach(element => meetingYearCount += element.count);

    this.setState({
      meetingTotal: meetingStats.meetingTotal,
      meetingTotalLastUpdate: lastUpdatedAt,
      meetingToday:
        meetingStats.meetingsLastMonth.data[
          meetingStats.meetingLastMonth.data.length - 1
        ].count,
      meetingTodayLastUpdate: lastUpdatedAt,
      meetingMonth: meetingMonthCount,
      meetingYear: meetingYearCount,
      meetingsLastMonth: meetingStats.meetingsLastMonth.data,
      meetingsLastYear: meetingStats.meetingsLastYear.data
    });
  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="Total Groups"
                statsValue={this.state.meetingTotal}
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText={`Last Update: ${this.state.meetingTotalLastUpdate}`}
              />
            </Col>

            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-users text-info" />}
                statsText="Groups Today"
                statsValue={this.state.meetingsToday}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText={`Last Update: ${this.state.meetingTodayLastUpdate}`}
              />
            </Col>
            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-users text-info" />}
                statsText="This Month"
                statsValue={this.state.meetingMonth}
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText={`Last Update: ${this.state.meetingTotalLastUpdate}`}
              />
            </Col>
            <Col lg={3} sm={6}>
              <KPICard
                bigIcon={<i className="pe-7s-users text-info" />}
                statsText="This Year"
                statsValue={this.state.groupYear}
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText={`Last Update: ${this.state.meetingTotalLastUpdate}`}
              />
            </Col>
          </Row>

          <Row>
            <Col lg={4} sm={6}></Col>
            <TotalGraph title="Total Groups" stroke="#228b22" signups={this.state.meetingsLastYear}/>
            <Col lg={4} sm={6}></Col>
          </Row>
          <Row>
            <Col lg={4} sm={6}>
              <LastMonthBar
                signups={this.state.meetingsLastMonth}
                title="Meetings Last Month"
              />
            </Col>
            <Col lg={4} sm={6}>
              <GroupSizeChart />
            </Col>
            <Col lg={4} sm={6}>
              <LastYearBar
                signups={this.state.meetingsLastYear}
                title="Meetings Last Year"
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    meetingsStats: state.KPI.meetingStats
  };
};

export default connect(mapStateToProps, { fetchMeetingStats })(GroupView);
