import React, { Component } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { Grid, Row, Col } from "react-bootstrap";

import { connect } from "react-redux";

import Sidebar from "../navigation/Sidebar";
import Header from "../navigation/Header";

import { fetchGroupsByNGO } from "../../redux/actions/ngo/NGOGroupsAction";

class NGOGroupView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {}
    };
  }

  componentDidMount() {
    this.fetchData();

    setInterval(async () => {
      this.fetchData();
    }, 10000000);
  }

  componentDidUpdate(prevProps) {
    if (this.props.data !== prevProps.data) {
      const data = this.props.data;

      const newState = this.props.groups.groupData.map(element => {
        return {
          name: element.name,
          cycle: element.cycle,
          meetings: element.meetings,
          shares: element.shares,
          loans: element.loans
        };
      });

      this.setState({
        data: newState
      });
    }
  }

  async fetchData() {
    await this.props.fetchGroupsByNGO('"FHIDO"');

    const newState = this.props.groups.groupData.map(element => {
      return {
        name: element.name,
        cycle: element.cycle,
        meetings: element.meetings,
        shares: element.shares,
        loans: element.loans
      };
    });

    this.setState({
      data: newState
    });

    // console.log(this.state.data);
  }
  render() {
    console.log(this.state.data);
    const groupData = [];

    // this.state.data.map(group => {
    //   return {
    //     id: 1,
    //     name: this.state.data.name,
    //     cycle: "",
    //     meetings: "",
    //     Shares: "",
    //     Loans: ""
    //   };
    // });

    const columns = [
      {
        dataField: "name",
        text: "Group Name"
      },
      {
        dataField: "cycle",
        text: "Cycle"
      },
      {
        dataField: "meeting",
        text: "Meetings"
      },
      {
        dataField: "share",
        text: "Shares"
      },
      {
        dataField: "loan",
        text: "Loans"
      },
      {
        dataField: "agent",
        text: "Agent"
      }
    ];
    return (
      <div className="wrapper">
        <Sidebar />

        <div id="main-panel" className="main-panel" ref="mainPanel">
          <Header
            title="NGOView
          "
          />
          <div className="content">
            <Grid fluid>
              <Row>
                <BootstrapTable
                  keyField="id"
                  data={groupData}
                  columns={columns}
                />
              </Row>
            </Grid>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    groups: state.NGO.groups
  };
};
connect(mapStateToProps);

export default connect(mapStateToProps, { fetchGroupsByNGO })(NGOGroupView);
