import axios from "axios";

import { GROUP_STATS } from "../ActionTypes";

const url = "/graphql";

export const fetchGroupStats = () => async dispatch => {
  const data = `query{
    groupStats{
      groupTotal
      groupSize{
        value
        count
      }
      groupsLastMonth{
        data{
          count
          day{
            year
            month
            day
          }
        }
      }
      groupsLastYear{
        data{
        year
        month
        count
        }
      }
      groupsCountry{
        name
        count
      }
      groupsNGO{
        name
        count
      }
    }
  }`;

  let response;

  try {
    response = await axios({
      url,
      method: "post",
      data: {
        query: data
      }
    });

    dispatch({
      type: GROUP_STATS,
      payload: response.data.data.groupStats
    });
  } catch (err) {
    console.log(err);
  }
};
