import React, { Component } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { Grid, Row, Col } from "react-bootstrap";

import { connect } from "react-redux";

import Sidebar from "../navigation/Sidebar";
import Header from "../navigation/Header";

import { fetchGroupsByNGO } from "../../redux/actions/ngo/NGOGroupsAction";

class NGOGroupView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {}
    };
  }

  componentDidMount() {
    this.fetchData();

    setInterval(async () => {
      this.fetchData();
    }, 10000000);
  }


  async fetchData() {
    await this.props.fetchGroupsByNGO('"FHIDO"');

    const newState = this.props.groups.groupData.map(element => {
      return {
        name: element.name,
        cycle: element.cycle,
        meetings: element.meetings,
        shares: element.shares,
        loans: element.loans
      };
    });

    this.setState({
      data: newState
    });
  }
  render() {
    let groupData;
    let id = 0;

    if (Array.isArray(this.state.data)) {
      groupData = this.state.data.map(group => {
        return {
          id: id++,
          name: group.name,
          cycle: group.cycle,
          meetings: group.meetings,
          shares: group.shares,
          loans: group.loans
        };
      });
    }

    const columns = [
      {
        dataField: "name",
        text: "Group Name"
      },
      {
        dataField: "cycle",
        text: "Cycle"
      },
      {
        dataField: "meetings",
        text: "Meetings"
      },
      {
        dataField: "shares",
        text: "Shares"
      },
      {
        dataField: "loans",
        text: "Loans"
      },
      {
        dataField: "agent",
        text: "Agent"
      }
    ];
    return (
      <div className="wrapper">
        <Sidebar />

        <div id="main-panel" className="main-panel" ref="mainPanel">
          <Header
            title="NGOView
          "
          />
          <div className="content">
            <Grid fluid>
              <Row>
                <BootstrapTable
                  keyField="id"
                  data={groupData ? groupData : []}
                  columns={columns}
                  striped
  hover
  condensed
                />
              </Row>
            </Grid>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    groups: state.NGO.groups
  };
};
connect(mapStateToProps);

export default connect(mapStateToProps, { fetchGroupsByNGO })(NGOGroupView);
